defmodule Lisp do
  def in_basement_at(_, index, sum) when sum < 0, do: index
  def in_basement_at("(" <> rest, index, sum),    do:  in_basement_at(rest, index + 1, sum + 1)
  def in_basement_at(")" <> rest, index, sum),    do:  in_basement_at(rest, index + 1, sum - 1)
  def in_basement_at(_, _, sum),                  do: sum

  def at_level("(" <> rest), do: at_level(rest) + 1
  def at_level(")" <> rest), do: at_level(rest) - 1
  def at_level(""),          do: 0
end

